use cortex_m_rt::{entry, exception};
use cortex_m_semihosting::{ debug, hio::{self, HStdout},
}; #[entry]
fn main() -> ! { let p = cortex_m::Peripherals::take().unwrap(); let mut syst = p.SYST; # // configures the system timer to trigger a SysTick exception every second // 毎秒SysTick例外を起こすためのシステムタイマを設定します syst.set_clock_source(SystClkSource::Core);
# // this is configured for the LM3S6965 which has a default CPU clock of 12 MHz // デフォルトのCPUクロックが12MHzのLM3S6965向けの設定です syst.set_reload(12_000_000); syst.enable_counter(); syst.enable_interrupt(); loop {}
